// src/components/PropellerSection.js
import React from "react";
import ParticlesBackground from "./ParticlesBackground";
import "../styles/styles.css";

const PropellerSection = () => {
  return (
    <section className="propeller-section">
      {/* Fondo de partículas en la sección */}
      <ParticlesBackground />
      {/* Contenedor de la tarjeta que muestra la información */}
      <div className="card-container">
        <div className="text-container">
          <h2 className="section-heading">Innovación en Energía Renovable</h2>
          <p className="section-description">
            Nuestro enfoque combina tecnología de drones y análisis avanzado para transformar la forma de inspeccionar y optimizar sistemas energéticos.
          </p>
          <button className="cta-button">Descubrir más</button>
        </div>
        <div className="animation-container">
          <img
            src="/assets/propeller.png"
            alt="Hélice Animada"
            className="propeller-image"
          />
        </div>
      </div>
    </section>
  );
};

export default PropellerSection;
